import React, { Fragment, useEffect, useState } from 'react';
import { Container, Col, Row } from 'react-bootstrap';
import { Scatter } from 'react-chartjs-2';
import { useSelector, useDispatch } from 'react-redux';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import faker from 'faker';
import { useAuth0 } from '@auth0/auth0-react';
import axios from 'axios';
import { addStock } from '../../redux/StockSlice2';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
);

export const options = {
  responsive: true,
  plugins: {
    legend: {
      position: 'top',
    },
  },
};

const Scatterchart = () => {
  const { user } = useAuth0();
  const { stocks2 } = useSelector((state) => state.second);
  const dispatch = useDispatch();
  const [sendData, setSendData] = useState([]);
  const [seriess, setSeriess] = useState([]);
  useEffect(() => {
    const id = user?.sub.split('|')[1];
    axios.get(`http://localhost:8000/datalist2/${33}`)
      .then((response) => {
        console.log(response.data, 'khjasdkjasdjadskadhkadsbhkahlasdhladhasdn');
        response.data.forEach((stock) => {
          console.log('stock', stock);
          dispatch(addStock(stock));
        });
      }, (error) => {
        console.log(error);
      });
  }, []);
  useEffect(() => {
    if (stocks2.length > 0) {
      setSendData([]);
      setSeriess([]);
      Object.entries(stocks2[0].response_data).map(([key, val]) => {
        if (key !== 'var' && key !== 'var_out' && key !== 'es' && key !== 'es_hist') {
          setSendData((e) => {
            console.log(val, 'etVal(val)');
            return [...e, {
              label: key,
              data: [{
                x: val.posEnd,
                y: val.stds,
              }],
              borderColor: 'rgba(82, 133, 80, 1)',
              backgroundColor: 'rgba(82, 133, 80, 0.5)',
            }];
          });
        }
        return val;
      });
    }
    console.log(stocks2, 'stocks2');
  }, [stocks2]);
  useEffect(() => {
    console.log(sendData, 'sendData');
  }, [sendData, seriess]);
  const labels = ['-20%', '-10%', '0%', '10%', '20%'];
  const data = {
    labels,
    datasets: sendData,
  };
  console.log(data, '==========================data');
  return (
    <>
      <Container>
        <Row>
          <Col className="mt-4">
            <p className="text-center font-weight-bold">Standard deviation-total returns</p>
          </Col>
        </Row>
        <Row>
          <Col>
            <Scatter options={options} data={data} style={{ height: '100%', width: '100%' }} />
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default Scatterchart;
