            if (!e[key].includes(val)) {
              e[key].push(val.toFixed(2));
            }
          } catch {
            e[key] = [val];
          }
          return e;
        });
        return val;
      });
    }
  }, [stocks]);
  console.log('stock1 Data', sendData);
