          console.log('shv====>', shv);
          setDataArray((e) => {
            console.log('');
            return [...e, shv];
          });
          return shv;
        });
        console.log('sv====>', sv);
        return sv;
      });
      console.log('v====>', v);
      return v;
    });
  }, [sendData]);
  console.log('DataArray is:', dataArray);

  const data = [{
    type: 'bar',
    x: [20, 14, 23],
    y: ['giraffes', 'orangutans', 'monkeys'],
    orientation: 'h',
  }];
  const layout = {
    xaxis: {
      autorange: 'reversed',
      tickangle: -45,
    },
    // yaxis: {
    //   side: 'left',
    // },
    yaxis: {
      zeroline: false,
      gridwidth: 3,
    },
    // bargap: 0.05,
  };

  // const data = [
