  //     type: 'pie',

  //     marker: {
  //       colors: ['rgb(56, 75, 126)', 'rgb(18, 36, 37)', 'rgb(34, 53, 101)'],
  //       width: 1,
  //     },
  //   },
  // ];

  return (
    <Plot
      data={data}
      layout={layout}
      // layout={{ width: 500, height: 500, title: 'How you have split your investment across companies' }}
    />
  );
};
