import React, { Fragment, useEffect, useState } from 'react';
import { Bar } from 'react-chartjs-2';
import { Container, Col, Row } from 'react-bootstrap';
import { useAuth0 } from '@auth0/auth0-react';
import { useSelector, useDispatch } from 'react-redux';


const  HBarchart = () => {
  const { user, isAuthenticated } = useAuth0();
  const [dataArray, setDataArray] = useState([]);
  const dispatch = useDispatch();
  const { stocks } = useSelector((state) => state.first);
  // useEffect(() => {
  //   console.log('dataArray', dataArray);
  // }, [dataArray]);
  const [sendData, setSendData] = useState({});
  const [seriess, setSeriess] = useState([]);
  useEffect(() => {
    if (stocks.length > 0) {
      setSendData({});
      Object.entries(stocks[0].response_data).map(([key, val]) => {
        setSendData((e) => {
          try {
            if (!e[key].includes(val)) {
              e[key].push(val.toFixed(2));
            }
          } catch {
            e[key] = [val];
          }
          return e;
        });
        return val;
      });
    }
  }, [stocks]);
  console.log('stock1 Data', sendData);
  useEffect(() => {
    Object.entries(sendData)?.map(([k, v]) => {
      Object.entries(v)?.map(([sk, sv]) => {
        Object.entries(sv)?.map(([shk, shv]) => {
          console.log('shv====>', shv);
          setDataArray((e) => {
            console.log('');
            return [...e, shv];
          });
          return shv;
        });
        console.log('sv====>', sv);
        return sv;
      });
      console.log('v====>', v);
      return v;
    });
  }, [sendData]);
  const data = {
    labels: Object.entries(sendData)?.map(([k, v]) => k),
    datasets: [
      {
        label: '# of Votes',
        data: dataArray,
        backgroundColor: [
          'rgba(255, 99, 132, 0.2)',
          'rgba(54, 162, 235, 0.2)',
          'rgba(255, 206, 86, 0.2)',
          'rgba(75, 192, 192, 0.2)',
          'rgba(153, 102, 255, 0.2)',
          'rgba(255, 159, 64, 0.2)',
        ],
        borderColor: [
          'rgba(255, 99, 132, 1)',
          'rgba(54, 162, 235, 1)',
          'rgba(255, 206, 86, 1)',
          'rgba(75, 192, 192, 1)',
          'rgba(153, 102, 255, 1)',
          'rgba(255, 159, 64, 1)',
        ],
        borderWidth: 1,
      },
    ],
  };

  const options = {
    plugins: {
      legend: { display: false },
    },
    indexAxis: 'y',
    maintainAspectRatio: false,
    responsive: false,
    scales: {
      yAxes: [
        {
          ticks: {
            beginAtZero: true,
          },
        },
      ],
    },
  };
  return (
    <Container>
      <Row>
        <Col>
          <p className="mt-5 p-3 font-weight-bold">
            <h5>Percentage return if you sold your stocks today</h5>
          </p>
        </Col>
      </Row>
      <Row>
        <Col className="mb-5">
          <Bar
            type="bar"
            data={data}
            options={options}
            height={188}
            width={375}
            className="mx-auto px-1"
          />
        </Col>
      </Row>
      <Row>
        <Col>
          <p className="text-center mb-2">Total amount invested</p>
          <p className="text-center font-weight-bold">$6638.35</p>
        </Col>
        <Col>
          <p className="text-center mb-2">Total return</p>
          <p className="text-center font-weight-bold">$168.2</p>
        </Col>
      </Row>
    </Container>
  );
};

export default HBarchart;
