import React, { Fragment, useEffect, useState } from 'react';
import { Container, Col, Row } from 'react-bootstrap';
import { Bar } from 'react-chartjs-2';
import Plot from 'react-plotly.js';
import { useSelector, useDispatch } from 'react-redux';
import axios from 'axios';
import { useAuth0 } from '@auth0/auth0-react';
import { selectStocks2 } from '../../redux/ConfigureStore';
import { addMarket } from '../../redux/MarketC';

const config = {
  type: 'bar',
  options: {
    plugins: {
      title: {
        display: true,
        text: 'Chart.js Bar Chart - Stacked',
      },
    },
    responsive: true,
    interaction: {
      intersect: false,
    },
    scales: {
      x: {
        stacked: true,
      },
      y: {
        stacked: true,
        suggestedMin: 0,
        suggestedMax: 100,
      },
    },
  },
};
const StackedChart = () => {
  const { user, isAuthenticated } = useAuth0();
  const [dataArray, setDataArray] = useState([]);
  const dispatch = useDispatch();
  const { market } = useSelector((state) => state.third);
  // useEffect(() => {
  //   console.log('dataArray', dataArray);
  // }, [dataArray]);
  const [sendData, setSendData] = useState([]);
  const [seriess, setSeriess] = useState([]);
  useEffect(() => {
    axios.get('http://localhost:8000/marketcomparison').then(
      (response) => {
        response.data.forEach((m) => dispatch(addMarket(m)));
        setSendData([]);
        response?.data?.map((value) => {
          const final = {};
          Object.entries(value)?.map(([key, val]) => {
            if (!key.includes('GSPC Volume') || !val === 0) {
              final[key] = val;
            }
            return val;
          });
          setSendData((e) => [...e, {
            label: 'Popularity of colours',
            data: final,
            backgroundColor: [
              'green',
            ],
            borderWidth: 1,
            width: '100%',
            height: '100%',
          }]);
          return value;
        });
      },
      (error) => {
        console.log(error);
      },
    );
  }, []);
  const data = {
    labels: [],
    datasets: sendData,
  };
  return (
    <Container>
      <Row>
        <Col className="mt-4 pb-5">
          <p className="text-center font-weight-bold">
            <h5>How your stocks performed compared to the whole US market </h5>
          </p>
          <Bar
            data={data}
            options={config}
            height={250}
          />
        </Col>
      </Row>
    </Container>
  );
};
export default StackedChart;
